fiddle.view:
    @using Sitecore.Mvc
@using Sitecore.Data.Items
@model Sitecore.Mvc.Presentation.RenderingModel
@{
  var control = this.InitializeControl();
  
  var dataSource = Model.Item;
}
<ul @control.HtmlAttributes>
  @foreach(Item item in dataSource.Children) {
  <li>
    @item.Name
  </li>
  }
</ul>
